import React from "react";
import "../stylesheets/App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="App">
        <h3>Películas</h3>
        <form action="">
          <label htmlFor="title">Título de la película</label>
          <input id="title" type="text" />
          <label htmlFor="description">Resumen de la película</label>
          <textarea
            name="description"
            id="description"
            cols="30"
            rows="10"
          ></textarea>
        </form>
      </div>
    );
  }
}

export default App;
